!function(a){
	jQuery.fn.tab=function(t,n){
		var i=a(".tab_btn"),e=a(".tab_content");
		t&&(i=t),n&&(e=n),
		i.on("click","[data-tab]",
			function(){
				var t=a(this).data("tab");
				a(this).trigger("change_tab"),
				e.find("[data-tab="+t+"]").trigger("change_tab")
			}).on("change_tab","[data-tab]",

				function(){

					i.find("[data-tab]").removeClass("active"),
					a(this).addClass("active")
				}),
				e.on("change_tab","[data-tab]",
					function(){
						e.find("[data-tab]").removeClass("active"),
						a(this).addClass("active")
				})
		}
}
(jQuery);